import React, { useState } from "react";
import "./App.css";

const Tooltip = ({ text, position, children }) => {
  const [showTooltip, setShowTooltip] = useState(false);

  const handleMouseEnter = () => {
    setShowTooltip(true);
  };

  const handleMouseLeave = () => {
    setShowTooltip(false);
  };

  return (
    <div
      className="tooltip-container"
      onMouseEnter={handleMouseEnter}
      onMouseLeave={handleMouseLeave}
    >
      {children}
      {showTooltip && (
        <div className={`tooltip tooltip-${position}`}>{text}</div>
      )}
    </div>
  );
};

const App = () => {
  return (
    <div className="App">
      <h1>Tooltip App</h1>
      <Tooltip text="This is a top tooltip" position="top">
        <button>Top</button>
      </Tooltip>
      <Tooltip text="This is a bottom tooltip" position="bottom">
        <button>Bottom</button>
      </Tooltip>
      <Tooltip text="This is a left tooltip" position="left">
        <button>Left</button>
      </Tooltip>
      <Tooltip text="This is a right tooltip" position="right">
        <button>Right</button>
      </Tooltip>
    </div>
  );
};

export default App;
