import React from "react";

const TooltipPreview = ({
  targetElement,
  tooltipText,
  textSize,
  padding,
  textColor,
  backgroundColor,
  cornerRadius,
  tooltipWidth,
  arrowWidth,
  arrowHeight,
}) => {
  const tooltipStyle = {
    fontSize: textSize + "px",
    padding: padding + "px",
    color: textColor,
    backgroundColor,
    borderRadius: cornerRadius + "px",
    width: tooltipWidth + "px",
    position: "absolute",
    display: "inline-block",
    zIndex: 9999,
  };

  const arrowStyle = {
    position: "absolute",
    width: 0,
    height: 0,
  };

  const positionTooltip = () => {
    let targetElementRef = document.getElementById(targetElement);
    if (!targetElementRef) return {};

    const targetRect = targetElementRef.getBoundingClientRect();

    const tooltipRect = {
      width: tooltipWidth,
      height: 0, // Actual height will be calculated based on text content
    };

    const arrowPosition = arrowWidth / 2;
    const arrowMargin = 5; // Additional margin between arrow and target element

    switch (targetElement) {
      case "B1":
        return {
          top: targetRect.top + targetRect.height + arrowHeight + arrowMargin,
          left: targetRect.left + arrowPosition,
          ...adjustArrowPosition("top"),
        };
      case "B2":
        return {
          top: targetRect.top + targetRect.height + arrowHeight + arrowMargin,
          right: window.innerWidth - targetRect.right + arrowPosition,
          ...adjustArrowPosition("top"),
        };
      case "B3":
        return {
          top: targetRect.top - tooltipRect.height - arrowHeight - arrowMargin,
          left: targetRect.left + arrowPosition,
          ...adjustArrowPosition("bottom"),
        };
      case "B4":
        return {
          top: targetRect.top - tooltipRect.height - arrowHeight - arrowMargin,
          left: targetRect.left + arrowPosition,
          ...adjustArrowPosition("bottom"),
        };
      case "B5":
        return {
          top: targetRect.top + targetRect.height + arrowHeight + arrowMargin,
          right: window.innerWidth - targetRect.right + arrowPosition,
          ...adjustArrowPosition("top"),
        };
      default:
        return {};
    }
  };

  const adjustArrowPosition = (direction) => {
    switch (direction) {
      case "top":
        return { borderBottomColor: backgroundColor };
      case "bottom":
        return { borderTopColor: backgroundColor };
      default:
        return {};
    }
  };

  return (
    <div className="tooltip-preview">
      <div
        className="target-element"
        id={targetElement}
      >
        {targetElement}
      </div>
      <div
        className="tooltip"
        style={{
          ...tooltipStyle,
          ...positionTooltip(),
        }}
      >
        <span
          className="tooltip-arrow"
          style={{
            ...arrowStyle,
            ...adjustArrowPosition("top"),
          }}
        />
        {tooltipText}
      </div>
    </div>
  );
};

export default TooltipPreview;
