import React from "react";

const TooltipForm = ({
  targetElement,
  tooltipText,
  textSize,
  padding,
  textColor,
  backgroundColor,
  cornerRadius,
  tooltipWidth,
  arrowWidth,
  arrowHeight,
  handleChange,
}) => {
  return (
    <div className="tooltip-form">
      <h2>Tooltip Settings</h2>
      <div>
        <label htmlFor="targetElement">Target Element:</label>
        <select
          id="targetElement"
          name="targetElement"
          value={targetElement}
          onChange={handleChange}
        >
          <option value="B1">B1</option>
          <option value="B2">B2</option>
          <option value="B3">B3</option>
          <option value="B4">B4</option>
          <option value="B5">B5</option>
        </select>
      </div>
      <div>
        <label htmlFor="tooltipText">Tooltip Text:</label>
        <input
          type="text"
          id="tooltipText"
          name="tooltipText"
          value={tooltipText}
          onChange={handleChange}
        />
      </div>
      {/* Add more input fields for other properties */}
      {/* Text Size, Padding, Text Color, Background Color, Corner Radius, Tooltip Width, Arrow Width, Arrow Height */}
    </div>
  );
};

export default TooltipForm;
