import React, { useRef, useEffect } from "react";
//tooltip preview
const TooltipPreview = ({
  targetElement,
  tooltipText,
  textSize,
  padding,
  textColor,
  backgroundColor,
  cornerRadius,
  tooltipWidth,
  arrowWidth,
  arrowHeight,
}) => {
  const tooltipRef = useRef(null);

  useEffect(() => {
    positionTooltip();
  }, [targetElement, textSize, padding, textColor, backgroundColor, cornerRadius, tooltipWidth, arrowWidth, arrowHeight]);

  const tooltipStyle = {
    fontSize: textSize + "px",
    padding: padding + "px",
    color: textColor,
    backgroundColor,
    borderRadius: cornerRadius + "px",
    width: tooltipWidth + "px",
    position: "absolute",
    display: "inline-block",
    zIndex: 9999,
  };

  const arrowStyle = {
    position: "absolute",
    width: 0,
    height: 0,
  };

  const positionTooltip = () => {
    const targetElementRef = document.getElementById(targetElement);
    const tooltipRefCurrent = tooltipRef.current;

    if (!targetElementRef || !tooltipRefCurrent) return;

    const targetRect = targetElementRef.getBoundingClientRect();
    const tooltipRect = tooltipRefCurrent.getBoundingClientRect();

    const arrowPosition = arrowWidth / 2;
    const arrowMargin = 5; // Additional margin between arrow and target element

    switch (targetElement) {
      case "B1":
        tooltipRefCurrent.style.top ="40px";
        tooltipRefCurrent.style.left = "15px";
        arrowStyle.borderBottomColor = "black";
        arrowStyle.borderRightColor = "transparent";
        arrowStyle.borderLeftColor = "transparent";
        break;
      case "B2":
        tooltipRefCurrent.style.top ="40px";;
        tooltipRefCurrent.style.left ="550px";
          arrowStyle.borderBottomColor = "black";
        arrowStyle.borderLeftColor = "transparent";
        arrowStyle.borderRightColor = "transparent";
        break;
      case "B3":
        tooltipRefCurrent.style.top ="230px";
        tooltipRefCurrent.style.left =
          targetRect.left + targetRect.width + arrowHeight + arrowMargin + "px";
          arrowStyle.borderBottomColor = "black";
        arrowStyle.borderTopColor = "transparent";
        arrowStyle.borderBottomColor = "transparent";
        break;
      case "B4":
        tooltipRefCurrent.style.top ="350px";
        tooltipRefCurrent.style.left = "15px";
          arrowStyle.borderBottomColor = "black";
        arrowStyle.borderRightColor = "transparent";
        arrowStyle.borderLeftColor = "transparent";
        break;
      case "B5":
        tooltipRefCurrent.style.top ="350px";
        tooltipRefCurrent.style.left ="550px";
          arrowStyle.borderBottomColor = "black";
        arrowStyle.borderLeftColor = "transparent";
        arrowStyle.borderRightColor = "transparent";
        break;
      default:
        break;
    }
  };

  return (
    <div className="tooltip-preview">
      <div className="target-container" id="target-container">
        <div className="target-element B1" id="B1">
          Button1
        </div>
        <div className="target-element B2" id="B2">
          Button2
        </div>
        <div className="target-element B3" id="B3">
          Button3
        </div>
        <div className="target-element B4" id="B4">
          Button4
        </div>
        <div className="target-element B5" id="B5">
          Button5
        </div>
      </div>
      <div
        className="tooltip"
        style={{
          ...tooltipStyle,
        }}
        ref={tooltipRef}
      >
        <span
          className="tooltip-arrow"
          style={{
            ...arrowStyle,
          }}
        />
        {tooltipText}
      </div>
    </div>
  );
};

export default TooltipPreview;
